import React from "react";
import { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";

// Usage of mondaySDK example, for more information visit here: https://developer.monday.com/apps/docs/introduction-to-the-sdk/
const monday = mondaySdk();

const App = () => {
  const [context, setContext] = useState([]);
  const [items, setItems] = useState([]);
  const [query, setQuery] = useState();

  monday.get("context").then(res => 
    setContext(res.data)
  );

  // useEffect(() => {
  //   monday.listen("context", (res) => {
  //     setContext(res.data);
  //   });
  // }, []);

  console.log(context);

  useEffect(() => {

    setQuery(`query { boards (ids: ${context.data.boardIds[0]}) { items (limit:100) { id name }}}`);

    fetch("https://api.monday.com/v2", {
      method: 'post',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjE3OTIzNzc0MCwidWlkIjozMzY5MzY2MiwiaWFkIjoiMjAyMi0wOS0wNVQyMjoxMToyOS4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTMyMzk5NjgsInJnbiI6InVzZTEifQ.gp5apVxi1EXSC2Uh6pIVCaRGoeLx6GIBWmoKOTdnHZQ'
      },
      body: JSON.stringify({
        query: query
      })
    })
      .then(res => console.log(res.json()));

  }, [context]);

  return (
    <div className="App">

    </div>
  );
};

export default App;
